import db from "../firebase/firebaseInit";
export default {
  name: "BoardPage",
  methods: {
    selectItem: function selectItem(index, item, arr) {
      if (this.debug) console.log("selectItem - Board Page");
      this.selectedIndex = index;
      this.selectedItem = item;
      this.selectedArr = arr;
      var selectedHtmlElOrig = document.querySelector("#" + item.id);
      this.selectedHtmlEl = selectedHtmlElOrig.cloneNode(true);
      this.selectedHtmlEl.style.width = selectedHtmlElOrig.clientWidth + "px";
      this.selectedHtmlEl.style.position = "fixed";
      var selectedHtmlClass = document.querySelectorAll("[class^=drop-wrapper]");
      this.oldSentArrayLength = selectedHtmlClass[0].childElementCount;
      this.oldReviewedArrayLength = selectedHtmlClass[1].childElementCount;
      this.oldInProgressArrayLength = selectedHtmlClass[2].childElementCount;
      this.oldDoneArrayLength = selectedHtmlClass[3].childElementCount;
      document.body.appendChild(this.selectedHtmlEl);
      this.isMouseDown = true;
    },
    dragItem: function dragItem(event) {
      if (!this.isDragging) return;
      if (this.debug) console.log("dragItem - Board Page");

      if (this.selectedHtmlEl) {
        var x = event.x - this.selectedHtmlEl.clientWidth / 2;
        var y = event.y - this.selectedHtmlEl.clientHeight - 5;
        this.selectedHtmlEl.style.top = y + "px";
        this.selectedHtmlEl.style.left = x + "px";
      }
    },
    dropItem: function dropItem(index, arr) {
      if (!this.isDragging) return;
      this.deleteItem(this.selectedIndex, this.selectedArr);
      if (this.debug) console.log("dropItem - Board Page");
      arr.splice(index, 0, this.selectedItem);
      this.isMouseDown = false;

      if (this.done.length > this.oldDoneArrayLength) {
        console.log("UPDATE DONE");
        this.updateStatus("DONE");
      } else if (this.inprogress.length > this.oldInProgressArrayLength) {
        console.log("UPDATE IN PROGRESS");
        this.updateStatus("IN PROGRESS");
      } else if (this.reviewed.length > this.oldReviewedArrayLength) {
        console.log("UPDATE REVIEWED");
        this.updateStatus("REVIEWED");
      } else if (this.sent.length > this.oldSentArrayLength) {
        console.log("UPDATE SENT");
        this.updateStatus("SENT");
      }
    },
    deleteItem: function deleteItem(index, arr) {
      if (this.debug) console.log("deleteItem - Board Page");
      arr.splice(index, 1);
    },
    isItemDragging: function isItemDragging(item) {
      if (this.isDragging && this.selectedItem && item.id === this.selectedItem.id) return true;
      return false;
    },
    addItem: function addItem(type) {
      if (!this.inputItemName) return;
      if (this.debug) console.log("addItem - Board Page");
      this.showAddingPopup = false;
      var item = {
        name: this.inputItemName,
        id: ""
      };

      switch (type) {
        case "Sent":
          item.id = type + this.sent.length;
          this.sent.push(item);
          break;

        case "Reviewed":
          item.id = type + this.reviewed.length;
          this.reviewed.push(item);
          break;

        case "InProgress":
          item.id = type + this.projects.length;
          this.inprogress.push(item);
          break;

        case "Done":
          item.id = type + this.tools.length;
          this.done.push(item);
          break;
      }

      this.inputItemName = "";
    },
    transformToShortText: function transformToShortText(text) {
      if (text.length > 100) return text.substring(0, 100) + "...";else return text;
    },
    // eslint-disable-next-line
    displayTicket: function displayTicket(index, item, arr) {
      this.showUpdatePopup = true;
      this.id = item.id;
      this.complaint_id = item.complaint_id;
      this.category = item.category;
      this.description = item.description;
      this.status = item.status;
      this.feedback = item.feedback;
      this.location = item.location;
      this.type = item.type;
      this.assignTo = item.assignTo;
      this.priority = item.priority;
      this.deadline = item.deadline;
    },
    updateTicket: function updateTicket() {
      db.collection("complaints").doc(this.id).update({
        assignTo: this.assignTo,
        priority: this.priority,
        deadline: this.deadline,
        category: this.category,
        status: this.status,
        feedback: this.feedback
      }).then(function () {
        console.log("Document successfully updated!");
      }).catch(function (error) {
        // The document probably doesn't exist.
        console.error("Error updating document: ", error);
      });
      this.showUpdatePopup = false;
      this.fetchData();
    },
    updateStatus: function updateStatus(value) {
      db.collection("complaints").doc(this.selectedItem.id).update({
        status: value
      }).then(function () {
        console.log("Document successfully updated!");
      }).catch(function (error) {
        // The document probably doesn't exist.
        console.error("Error updating document: ", error);
      });
    },
    fetchData: function fetchData() {
      var _this = this;

      this.sent = [];
      this.reviewed = [];
      this.inprogress = [];
      this.done = [];
      db.collection("complaints").where("status", "==", "SENT").get().then(function (querySnapshot) {
        _this.loading = false;
        querySnapshot.forEach(function (doc) {
          var data = {
            id: doc.id,
            complaint_id: doc.data().id,
            category: doc.data().category,
            feedback: doc.data().feedback,
            status: doc.data().status,
            type: doc.data().type,
            description: doc.data().description,
            timestamp: doc.data().timestamp,
            location: doc.data().location,
            assignTo: doc.data().assignTo,
            priority: doc.data().priority,
            deadline: doc.data().deadline
          };

          _this.sent.push(data);
        });
      });
      db.collection("complaints").where("status", "==", "REVIEWED").get().then(function (querySnapshot) {
        _this.loading = false;
        querySnapshot.forEach(function (doc) {
          var data = {
            id: doc.id,
            complaint_id: doc.data().id,
            category: doc.data().category,
            feedback: doc.data().feedback,
            status: doc.data().status,
            type: doc.data().type,
            description: doc.data().description,
            timestamp: doc.data().timestamp,
            location: doc.data().location,
            assignTo: doc.data().assignTo,
            priority: doc.data().priority,
            deadline: doc.data().deadline
          };

          _this.reviewed.push(data);
        });
      });
      db.collection("complaints").where("status", "==", "IN PROGRESS").get().then(function (querySnapshot) {
        _this.loading = false;
        querySnapshot.forEach(function (doc) {
          var data = {
            id: doc.id,
            complaint_id: doc.data().id,
            category: doc.data().category,
            feedback: doc.data().feedback,
            status: doc.data().status,
            type: doc.data().type,
            description: doc.data().description,
            timestamp: doc.data().timestamp,
            location: doc.data().location,
            assignTo: doc.data().assignTo,
            priority: doc.data().priority,
            deadline: doc.data().deadline
          };

          _this.inprogress.push(data);
        });
      });
      db.collection("complaints").where("status", "==", "DONE").get().then(function (querySnapshot) {
        _this.loading = false;
        querySnapshot.forEach(function (doc) {
          var data = {
            id: doc.id,
            complaint_id: doc.data().id,
            category: doc.data().category,
            feedback: doc.data().feedback,
            status: doc.data().status,
            type: doc.data().type,
            description: doc.data().description,
            timestamp: doc.data().timestamp,
            location: doc.data().location,
            assignTo: doc.data().assignTo,
            priority: doc.data().priority,
            deadline: doc.data().deadline
          };

          _this.done.push(data);
        });
      });
    },
    changeCategory: function changeCategory(event) {
      this.category = event.target.value;
    },
    changeStatus: function changeStatus(event) {
      this.status = event.target.value;
    },
    changeAssignTo: function changeAssignTo(event) {
      this.assignTo = event.target.value;
    },
    changePriority: function changePriority(event) {
      this.priority = event.target.value;
    }
  },
  created: function created() {
    var _this2 = this;

    var showMouseMove = false;
    window.addEventListener("mousemove", function (event) {
      if (_this2.debug && showMouseMove) console.log("mouseMove - Board Page");
      if (_this2.isMouseDown) _this2.isDragging = true;else _this2.isDragging = false;

      _this2.dragItem(event);
    });
    window.addEventListener("mouseup", function () {
      if (_this2.debug) console.log("mouseUp - Board Page");
      if (_this2.selectedHtmlEl && _this2.selectedHtmlEl.parentNode) _this2.selectedHtmlEl.parentNode.removeChild(_this2.selectedHtmlEl);
      _this2.isMouseDown = false;
    });
    this.fetchData();
  },
  data: function data() {
    return {
      debug: true,
      isMouseDown: false,
      isDragging: false,
      selectedIndex: null,
      selectedItem: null,
      selectedHtmlEl: null,
      selectedArr: null,
      sent: [],
      reviewed: [],
      inprogress: [],
      done: [],
      showAddingPopup: false,
      showUpdatePopup: false,
      addPopupTitle: "",
      inputItemName: "",
      id: "",
      complaint_id: "",
      status: "",
      category: "",
      description: "",
      feedback: "",
      type: "",
      location: "",
      priority: "",
      assignTo: "",
      deadline: "",
      oldSentArrayLength: null,
      oldReviewedArrayLength: null,
      oldInProgressArrayLength: null,
      oldDoneArrayLength: null,
      newSentArrayLength: null,
      newReviewedArrayLength: null,
      newInProgressArrayLength: null,
      newDoneArrayLength: null,
      categories: ["Airport", "Bike lane", "Bridge", "Bus", "Canal", "Field", "Park", "Parking Area", "Port", "Road", "Sidewalk", "Traffic sign", "Train", "Tram", "Tunnel", "Waterway", "Other"],
      statusList: ["SENT", "REVIEWED", "IN PROGRESS", "DONE"],
      thirdPartyList: [{
        name: "Julia Rakowiecka",
        image: "https://assets.dryicons.com/uploads/icon/svg/5632/0e5980e9-9549-4690-96be-57c04c9477b4.svg"
      }, {
        name: "Ikhsan Habibi",
        image: "https://assets.dryicons.com/uploads/icon/svg/5582/e348412b-7b94-409b-9a9e-6884632dccde.svg"
      }, {
        name: "Max Musterman",
        image: "https://assets.dryicons.com/uploads/icon/svg/5579/0f02d8b8-1178-4c83-a3ff-2ec291c76f73.svg"
      }],
      priorityList: ["HIGH", "MEDIUM", "LOW"]
    };
  }
};
